$('a[href*="#"]').not('[href="#"]').not('[href="#0"]').click((function(event){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var target=$(this.hash);(target=target.length?target:$("[name="+this.hash.slice(1)+"]")).length&&(event.preventDefault(),$("html, body").animate({scrollTop:target.offset().top},600))}}));const project_item=({image:image,title:title,description:description,project_url:project_url})=>`\n  <div class="card text-white bg-dark mb-3 project-card">          \n  <div class="card-body">\n    <div class="row">\n      <div class="col-auto">\n        <div class="card-img">\n          <img src="${image}" alt="image">\n        </div>\n      </div>\n      <div class="col">\n        <div class="container card-body card-body-big">\n          <h3 class="card-title">${title}</h3>\n            ${description}\n        </div>\n        <div class="container-fluid">\n            <a href="${project_url}" target="_blank" class="btn btn-dark float-right">Ver Proyecto <i class="fa fa-arrow-right"></i></a>\n        </div>\n      </div>\n    </div>\n  </div>\n</div>\n  `;$(document).ready((function(){$.getJSON("./projects.json",(function(projects){var profesional=projects["profesional-projects"];$("#pro-projects-container").html(profesional.map(project_item).join(""));var personal=projects["personal-projects"];$("#per-projects-container").html(personal.map(project_item).join(""));var elm=$(".project-card");elm.mouseenter((function(event){$(event.currentTarget).find("img").transition({scale:1.2},400)})),elm.mouseleave((function(event){$(event.currentTarget).find("img").transition({scale:1},400)}))}))}));