$('a[href*="#"]').not('[href="#"]').not('[href="#0"]').click(function(e){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var t=$(this.hash);(t=t.length?t:$("[name="+this.hash.slice(1)+"]")).length&&(e.preventDefault(),$("html, body").animate({scrollTop:t.offset().top},600))}});var elm=$(".card-img").children("img");elm.mouseenter(function(e){$(e.currentTarget).transition({scale:1.2},300)}),elm.mouseleave(function(e){$(e.currentTarget).transition({scale:1},300)});